CREATE TABLE user (id INTEGER PRIMARY KEY AUTOINCREMENT, username VARCHAR(30) NOT NULL UNIQUE, password VARCHAR(100) NOT NULL, role VARCHAR(20) DEFAULT 'guest' NOT NULL, isactive INTEGER DEFAULT '0', catatan sring(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE semester (id INTEGER PRIMARY KEY AUTOINCREMENT, kode VARCHAR(15) NOT NULL UNIQUE, tahun_ajaran VARCHAR(9), semester VARCHAR(255), awal_ajaran DATE, akhir_ajaran DATE, catatan VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE bip (id INTEGER PRIMARY KEY AUTOINCREMENT, kode VARCHAR(3) NOT NULL UNIQUE, nama VARCHAR(30) NOT NULL UNIQUE, keterangan VARCHAR(255), catatan VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE prestasi_siswa (id INTEGER PRIMARY KEY AUTOINCREMENT, nis INTEGER NOT NULL, semester VARCHAR(15) NOT NULL, kegiatan VARCHAR(50), prestasi VARCHAR(30) NOT NULL, keterangan TEXT, catatan VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE big (id INTEGER PRIMARY KEY AUTOINCREMENT, nip INTEGER NOT NULL, nama VARCHAR(50) NOT NULL, kode VARCHAR(2) NOT NULL UNIQUE, catatan sring(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE bik (id INTEGER PRIMARY KEY AUTOINCREMENT, nik INTEGER NOT NULL, nama VARCHAR(50) NOT NULL, jk VARCHAR(255), tugas VARCHAR(255), catatan sring(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE status_siswa (id INTEGER PRIMARY KEY AUTOINCREMENT, nis INTEGER NOT NULL, kode_semester VARCHAR(15) NOT NULL, status VARCHAR(10), catatan VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE bis (id INTEGER PRIMARY KEY AUTOINCREMENT, nis INTEGER NOT NULL, nama VARCHAR(50) NOT NULL, jk VARCHAR(255), catatan sring(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE INDEX nis_idx ON bis (nis);
